import { useEffect } from "react";
import { MovieCard } from "./MovieCard";
import styles from "../styles/MoviesGrid.module.css";

export function MoviesGrid() {
    // let movies = [];
    const moviesState = useState([]);
    
    const movies = moviesState[0];
    const setMovies = moviesState[1]

    useEffect(() => [
        fetch("https://api.themoviedb.org/3/movie/76341/movie", {
            headers: {
                Authorization: 
                    "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI2YjkzOGFlNmJiZDAyOWJlNWJhMjZmYTI0NjNkODg5YyIsInN1YiI6IjYxMWJmNjY0OWYwZTE5MDA1YzNiOWFmZSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.vxy3eyEKAh9loDSyMEUFGbf1jWdFmmAQ08RPXOj4ZdU",
                "Content-Type": "application/json;charset=utf-8",
            },
        })
        .then(result => result.json())
        .then((data) => {
            movies = data.results;
            console.log(movies);
        }),
    ]); 
    return (
        <ul  className={styles.moviesGrid}>
            {movies.map((movie) => (
                <MovieCard key={movie.id} movie={movie}/>
            ))}
        </ul>
    );
}
